import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [posts, setPosts] = useState([]);

    useEffect(()=>{
        getPosts(1);
        generatePageBtn()
    }, [])

    function getPosts(url, page){
        apiCall('/post/'+url+'/'+page+'?limit=5', 'get').then(res=>{
            setPosts(res.data)
            
        })
    }

    function getPostByPageNum(num){

    }

    function generatePageBtn(posts){
        let a = posts[0]?.pageCount;
        let b = [];

        for(let i = 1; i<=a; i++){
            b.push(i);
        }

        return (
            <div>
                {
                    b.map(num=> <button  className="btn btn-info m-2">{num}</button>)
                }
            </div>
        )
    }


    return (
        <div>
            {
                generatePageBtn(posts)
            }
        </div>
    )
}

export default Home;
