import React, { useEffect, useState } from 'react';
import { apiCall } from '../utils/apiCall'

function AdminPage() {

    const [users, setUsers] = useState([]);
    const [roles, setRoles] = useState([]);

    useEffect(() => {
        getUsers();
        getRoles();
    }, [])

    function getUsers() {
        apiCall("/user", 'get').then(res => {
            console.log(res.data);
            setUsers(res.data)
        })
    }

    function getRoles() {
        apiCall("/user/roles", 'get').then(res => {
            setRoles(res.data)
        })
    }

    return (
        <div className='row'>
            <div className="container">
                <div>
                    <select name="" id="">
                        <option value="">All Roles</option>
                        {
                            roles.map(role=><option value={role.id}>{role.name}</option>)
                        }
                    </select>
                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>username</th>
                            <th>email</th>
                            <th>enabled</th>
                            <th>roles</th>
                            <th>actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => <tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td>
                                    <input type="checkbox" checked={user.enabled} />
                                </td>
                                <td>
                                    {
                                        user.roles.map(role => <p>{role.name} <input type="checkbox" checked={role.hasrole} /></p>)
                                    }
                                </td>
                                <td>
                                    <button className="btn btn-success mx-1">edit</button>
                                </td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default AdminPage;
