import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPosts } from '../store/slice/PostReducer';

function Posts() {

    const { posts } = useSelector(state => state.postReducer);
    const { currentUserId } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getPosts(currentUserId));
        console.log(posts);

    }, [currentUserId])

    return (
        <div className='row'>
            {
                posts.map((post, index)=> <div className='card col-3 mx-4' key={post.id}>
                    <div className="card-header">
                        <h5>{post.title}</h5>
                    </div>
                    <div className="card-body">
                        {post.body}
                    </div>
                    <div className="card-footer">
                        <button className="btn btn-success mx-1">edit</button>
                        <button className="btn btn-danger mx-1">delete</button>
                    </div>
                </div>)
            }
        </div>
    );
}

export default Posts;
