import React from "react";
import {apiCall} from './utils/apiCall'
import { useNavigate } from "react-router-dom";

function Login() {

    const navigate = useNavigate();

    function login(e){
        e.preventDefault();
        apiCall('/user/login', 'post', {email: e.target[0].value, password: e.target[1].value}).then(res=>{
            localStorage.setItem("userId", JSON.stringify(res.data.data.id));
            if(res.data.message === 'success'){
                navigate('/home')
            }
        })
    }

    return (
        <div className="container">
            <div className="card col-6 mt-5">
                <form onSubmit={login} action="">
                    <div className="card-header">
                        <h5 className="text-center">Login</h5>
                    </div>
                    <div className="card-body">
                        <input type="text" placeholder="Email..." className="form-control my-2" />
                        <input type="text" placeholder="Password..." className="form-control my-2" />
                    </div>
                    <div className="card-footer">
                        <a href="http://localhost:3000/register">Register</a>
                        <button className="btn btn-warning w-100 my-2">Submit</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Login;
