import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { addLoan, getUserSuccess, getUsers } from "../slice/userReducer.js";
import { addPayment } from "../slice/paymentReducer.js";

export function* workGetUsers(action){
    const res = yield call(apiCall, "/auth/all", "get");
    yield put(getUserSuccess(res.data))
}

export function* workAddLoan(action){
    yield call(apiCall, "/loan", "post", action.payload.data);
    yield put(getUsers())
}

export function* workAddPayment(action){
    yield call(apiCall, "/payment", "post", action.payload.data);
    yield put(getUsers())
}

export function* userSaga() {
    yield takeLatest(getUsers().type, workGetUsers);
    yield takeLatest(addLoan().type, workAddLoan);
    yield takeLatest(addPayment().type, workAddPayment)
}
