import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeCompleted, getTodos } from '../store/slice/todoReducer';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { toggle } from '../store/slice/todoReducer';

function Todos() {

    const {todos, visible} = useSelector(state => state.todoReducer);
    const {currentUserId} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getTodos(currentUserId))
    }, [currentUserId])

    return (
        <div>
            <div>
                {
                    todos.map((todo)=> <div className='todo' key={todo.id}>
                        <p>{todo.title}</p>
                        <input type="checkbox" onChange={(e)=> dispatch(changeCompleted({id: todo.id, userId: todo.userId, completed: e.target.checked}))} checked={todo.completed} />
                    </div>)
                }
            </div>
            <div>
                <button onClick={()=>dispatch(toggle())} className='btn btn-warning my-3 mx-3'>add todo +</button>
            </div>

            <Rodal visible={visible} onClose={()=>dispatch(toggle())}>
                <form>
                    <input type="text" className='form-control' />
                    <button className="btn btn-success my-2 w-100">submit</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Todos;
