import {createSlice} from "@reduxjs/toolkit";

const state = {
    lostItems: []
};

const slice = createSlice({
    name: "lostItem",
    initialState: state,
    reducers: {
        getLostItems(state,action){},
        getLostItemsSuccess(state,action){
            state.lostItems = action.payload;
        },
        saveLostItems(state,action){}
    }
})

export const {
    getLostItems,
    getLostItemsSuccess,
    saveLostItems
} = slice.actions;
export default slice.reducer;
