import React, { useEffect, useState } from "react";
import {apiCall} from './utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import 'bootstrap-icons/font/bootstrap-icons.css'

function App(){

    const [tasks, setTasks] = useState([]);
    const [categories, setCategories] = useState([]);
    const [visible, setVisible] = useState(false);
    const [editCategoryName, setEditCategoryName] = useState({id: 0, edit: false});

    useEffect(()=>{
        getCategories();
        getTasks();
    }, [])

    function getCategories(){
        apiCall('/category', 'get').then(res=>{
            setCategories(res.data)
            console.log(res.data);
        })
    }

    function toggle(){
        setVisible(prev=>!prev)
    }

    function getTasksById(id){
        apiCall('/task/'+id, 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function getTasks(id){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function submit(e){
        e.preventDefault();

        if(editCategoryName.edit){
            apiCall("/category", 'put', {id: editCategoryName.id, name: e.target[0].value}).then(res=>{
                getCategories();
            })
        }else{
            apiCall("/category", 'post', {name: e.target[0].value}).then(res=>{
                getCategories();
            })
        }
        
    }

    function editCategory(category){
        let categoryForm = document.getElementById('category');
        categoryForm[0].value = category.name;  
        setEditCategoryName({id: category.id, edit: true})
        toggle();
    }

    return (
        <div className="container">
            <div className="row">
                <div className="sider col-3">
                    <button onClick={toggle} className="btn btn-warning w-100 my-2">add Category</button>
                    {
                        categories.map(category=> <div className="card">
                            <p className="card-header d-flex justify-content-between">{category.name} <i onClick={()=>editCategory(category)} className="bi bi-pencil"></i></p>
                        </div>)
                    }
                </div>
            </div>

            <Rodal height={140} visible={visible} onClose={toggle} >
                <form id="category" onSubmit={submit} action="">
                    <input type="text" placeholder="Name..." className="form-control mt-3" />
                    <button className="btn btn-warning w-100 my-2">submit</button>
                </form>
            </Rodal>
        </div>
    )
}

export default App;
