import React, { useEffect, useState } from 'react';
import {apiCall} from './utils/apiCall'


function App(){

    const [todos, setTodos] = useState([]);


    useEffect(()=>{
        getTodos();
    }, [])

    function getTodos(){
        apiCall('/todo', 'get').then(res=>{
            setTodos(res.data)
            console.log(res.data);
        })
    }


    return (
        <div>
            <table className="table">
                <thead>
                    <th></th>
                    <th>name</th>
                    <th>description</th>
                    <th>status</th>
                    <th>actions</th>
                </thead>
                <tbody>
                    {
                        todos.map(todo=> <tr>
                            <td>
                                <input type="checkbox" checked={todo.status} />
                            </td>
                            <td>{todo.name}</td>
                            <td>{todo.description}</td>
                            <td>
                                {
                                    todo.status ? <button className='btn btn-success'>Completed</button> : <button className='btn btn-danger'>Pending</button>
                                }
                            </td>
                            <td>
                                <button className='btn btn-success mx-1'>edit</button>
                                <button className='btn btn-danger mx-1'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    )
}

export default App;
