import React, { useEffect, useState } from 'react';
import { apiCall } from '../utils/apiCall'

function AdminPage() {

    const [users, setUsers] = useState([]);
    const [roles, setRoles] = useState([]);
    const [user, setUser] = useState({});


    useEffect(() => {
        getUsers();
        getRoles();
    }, [])

    function getUsers() {
        apiCall("/user", 'get').then(res => {
            console.log(res.data);
            setUsers(res.data)
        })
    }

    function getRoles() {
        apiCall("/user/roles", 'get').then(res => {
            setRoles(res.data)
        })
    }

    function searchUser() {
        let search = document.getElementById('search');
        apiCall('/user/search?search=' + search.value, 'get').then(res => {
            setUsers(res.data)
        })
    }

    function selectRole(e) {
        apiCall('/user/filter-role?role=' + e.target.value, 'get').then(res => {
            setUsers(res.data);
        })
    }

    function selectStatus(e) {
        apiCall('/user/status?status=' + e.target.value, 'get').then(res => {
            setUsers(res.data)
        })
    }

    function changeEnable(id) {
        apiCall('/user/enable/' + id, 'put').then(res => {
            getUsers();
        })
    }

    function addRole(userId, roleId) {
        apiCall('/user/delete-role/' + userId + "/" + roleId, "put").then(res => {
            getUsers();
        })
    }

    function submit(e){
        if(user.id){
            apiCall('/user/update/'+user.id, 'put', {fullName: e.target[0].value, email: e.target[1].value}).then(res=>{
                getUsers();
                e.target[0].value = '';
                e.target[1].value = '';
                setUser({});
            })
        }
    }

    function edit(user){
        setUser(user)
        let form = document.getElementById('form');
        form[0].value = user.fullName;
        form[1].value = user.email;
    }

    return (
        <div className='row'>
            <div className="container">
                <div>
                    <select onChange={selectRole} name="" id="">
                        <option value="">All Roles</option>
                        {
                            roles.map(role => <option value={role.name}>{role.name}</option>)
                        }
                    </select>
                    <select onChange={selectStatus} name="" id="">
                        <option value="">all</option>
                        <option value="true">enabled</option>
                        <option value="false">disabled</option>
                    </select>
                    <input type="text" placeholder='Username...' id='search' />
                    <button className='btn btn-info m-1' onClick={searchUser}>send</button>

                    <div className='card-header col-2'>
                        <form id='form' onSubmit={submit} action="">
                            <input type="text" placeholder='fullname' className='form-control' />
                            <input type="text" placeholder='email' className='form-control' />
                            <button className="btn btn-warning my-1 w-100">submit</button>
                        </form>
                    </div>

                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>username</th>
                            <th>email</th>
                            <th>enabled</th>
                            <th>roles</th>
                            <th>actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => <tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.username}</td>
                                <td>{user.email}</td>
                                <td>
                                    <input onChange={() => changeEnable(user.id)} type="checkbox" checked={user.enabled} />
                                </td>
                                <td>
                                    {
                                        user.roles.map(role => <p>{role.name} <input onChange={() => addRole(user.id, role.id)} type="checkbox" checked={role.hasrole} /></p>)
                                    }
                                </td>
                                <td>
                                    <button onClick={()=>edit(user)} className="btn btn-success mx-1">edit</button>
                                </td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default AdminPage;
