import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from './store/slice/userReducer';

function App() {

    const {users} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getUsers());
        console.log(users);
    }, [])


    return (
        <div>
            <div>
                <select>
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                </select>
                <input type="text" />
                <button>send</button>
            </div>
            <div>
                <div>
                    from: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                    to: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                </div>
            </div>
        </div>
    );
}

export default App;
