import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, getMessages } from '../store/slice/messageReducer';
import { getUserChats } from '../store/slice/chatReducer';
import { setConnectedUser } from '../store/slice/userReducer';

function Content() {

    const {selectedUser, currentUser, connectedUser} = useSelector(state => state.userReducer);
    const {chats} = useSelector(state => state.chatReducer);
    const dispatch = useDispatch();
    const {messages} = useSelector(state => state.messageReducer);

    function send(){
        const value = document.getElementById("message").value;
        let data = {chatId: selectedUser.chatId ? selectedUser.chatId : "", initiatorId: currentUser.id, participantId: selectedUser.userId ? selectedUser.userId : selectedUser.id, messageBody: value }
        dispatch(addMessage(data));
        if(currentUser.id && currentUser.userId){
            dispatch(getMessages(currentUser.id))
            dispatch(getUserChats(currentUser.id));
        }
        // dispatch(setConnectedUser())
    }

    return (
        <div className="col-8">
            <div>
                <h1>Messages</h1>
                {
                    messages
                }
            </div>
            <div>
                <input type="text" id='message' />
                <button className='btn btn-success' onClick={send}>send</button>
            </div>
        </div>
    );
}

export default Content;
