import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeOrderproduct, deleteOrder } from '../store/slice/userReducer';

function OrderProduct() {

    const {orderProducts} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    let [totalBalance, setTotalBalance] = useState(0);

    useEffect(()=>{
        getTotalBalance("plus");
    }, [])

    function increment( index){
        dispatch(changeOrderproduct({status: "plus", index}))
        getTotalBalance("plus")
    }

    function decrement( index){
       dispatch(changeOrderproduct({status: "minus", index}))
       getTotalBalance("minus")

    }

    function orderDelete(index, order){
        dispatch(deleteOrder(index))
        getTotalBalance("delete")
    }

    function getTotalBalance(s){
        setTotalBalance(0)
        for(let i = 0; i < orderProducts.length; i++){
            if(s === "plus"){
                totalBalance  += orderProducts[i].amount * orderProducts[i].count;
            }else if( s === "minus"){
                totalBalance  -= orderProducts[i].amount * orderProducts[i].count;
            }
            setTotalBalance(totalBalance)
        }
    }



    return (
        <div>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>amount</th>
                        <th>price</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        orderProducts.map((order, index)=> <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.name}</td>
                            <td>{order.amount * order.count}</td>
                            <td>
                                <button onClick={()=> increment (index)} className='btn btn-warning mx-1'>+</button>
                                {order.count} 
                                <button onClick={()=> decrement(index)} className='btn btn-warning mx-1'>-</button> 
                                </td>
                            <td>
                                <button onClick={()=> orderDelete(index, order)} className='btn btn-danger'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>

            <div>
                <h5>total balance: {totalBalance}</h5>
            </div>
        </div>
    );
}

export default OrderProduct;
