import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getCurrentUser, getCurrentUserSuccess } from "../slice/userReducer.js";

export function* workGetCurrentUser(action){
    const res = yield call(apiCall, "/user/login", "post", action.payload);
    if(res.data.id !== null){
        console.log(res.data);
        yield put(getCurrentUserSuccess(res.data))
    }
}

export function* userSaga() {
    yield takeLatest(getCurrentUser().type, workGetCurrentUser)
}
