import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall'

function App() {

    const [users, setUsers] = useState([]);
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        getUsers();
        getTasks();
    }, [])



    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data)
            console.log(res.data);
        })
    }

    function getTasks() {
        apiCall('/task/user', 'get').then(res => {
            setTasks(res.data)
            console.log(res.data);
        })
    }

    return (
        <div>
            <div></div>
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>description</th>
                            <th>status</th>
                            <th>firstname</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map(task=> <tr>
                                <td>{task.id}</td>
                                <td>{task.title}</td>
                                <td>{task.description}</td>
                                <td>{task.status}</td>
                                <td>{task.firstName}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default App;
