import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getOldUsers, getTwoRich, getUsers, searchUsers } from './store/slice/userReducer';

function App() {

    const {users, totalPages} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const [paginate, setPaginate] = useState([])


    useEffect(()=>{
        dispatch(getUsers({limit: 5, pageIndex: 1}));
        generateButton();
    }, [])


    function generateButton(){
        for(let i = 1; i <= totalPages; i++){
            if(paginate.length < totalPages){
                paginate.push(i);
            }
        }
        setPaginate([...paginate]);
    }

    function search(){
        let input = document.getElementById('input').value;
        dispatch(searchUsers({limit: 5, pageIndex: 1}))
    }

    return (
        <div className='row'>
            <div className='col-3'>
                <button onClick={()=>dispatch(getUsers({limit: 5, pageIndex: 1}))} className="btn btn-warning w-100 my-2 mx-3">All</button>
                <button onClick={()=>dispatch(getTwoRich(5))} className='btn btn-warning w-100 my-2 mx-3'>Two Rich</button>
                <button onClick={()=>dispatch(getOldUsers({age: 50, limit: 5, pageIndex: 1}))} className='btn btn-warning w-100 my-2 mx-3'>Old Users</button>
            </div>
            <div className="col-8 m-3">
                <div>
                    <h1>Header</h1>
                    <input type="text" id='input' />
                    <button onClick={()=>search()} className='btn btn-warning'>send</button>
                </div>
                <div>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>t/r</th>
                                <th>full Name</th>
                                <th>phone Number</th>
                                <th>age</th>
                                <th>money</th>
                                <th>address</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                users.map((user, index)=> <tr key={user.id}>
                                    <td>{index+1}</td>
                                    <td>{user.fullName}</td>
                                    <td>{user.phoneNumber}</td>
                                    <td>{user.age}</td>
                                    <td>{user.moneyAmount}</td>
                                    <td>{user.address}</td>
                                </tr>)
                            }
                        </tbody>
                    </table>
                </div>
                <div className='d-flex justify-content-center'>
                    {
                        paginate.map(page=><button onClick={()=>dispatch(getUsers({limit: 5, pageIndex: page}))} className='btn btn-success mx-1' key={page}>{page}</button>)
                    }
                </div>
            </div>
        </div>
    );
}








export default App;
