import React, { useEffect, useState } from 'react';
import { apiCall } from '../utils/apiCall';

function Adminpage() {

    const [advertises, setAdvertises] = useState([]);

    useEffect(()=>{
        getAdminAdvertise();
    }, [])
    
    function getAdminAdvertise(){
        apiCall('/api/lost/admin-page', 'get').then(res=>{
            setAdvertises(res.data);
            console.log(res.data);
        })
    }

    function changeFound(id, foundStatus){
        apiCall("/api/lost/admin/"+id, 'put', {foundStatus}).then(res=>{
            console.log('change boldi');
        })
    }

    return (
        <div>
            <div>   
                {
                    advertises?.map(advertise=><div key={advertise.id} className='card col-5'>
                        <div className="card-header">
                            <h5>{advertise.id}) {advertise.registry}</h5>
                            <p>{advertise.description}</p>
                        </div>
                        <div className="card-body">
                            <div>Buyum yoki Hujjat holati: {advertise.status}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs ismi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs ismi" : ""}: {advertise.firstName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs familyasi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs familyasi" : ""}: {advertise.lastName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs telefon raqami" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs telefon raqami" : ""}: {advertise.phone_number}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Yo'qotib qo'ygan shaxs" : advertise.status === "TOPIB_OLINGAN" ? "Topib olgan shaxs" : ""}: {advertise.userFirstName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "yo'qotib qo'ygan shaxs familyasi" : advertise.status === "TOPIB_OLINGAN" ? "Topib olgan shaxs familyasi" : ""}: {advertise.userLastName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "yo'qotib qo'ygan shaxs telefon raqami" : advertise.status === "TOPIB_OLINGAN" ? "Topib olgan shaxs telefon raqami" : ""}: {advertise.userPhoneNumber}</div>
                            
                        </div>
                        <div className="card-footer">
                            <p>Topilgan deb belgilash!</p>
                            <button onClick={()=>changeFound(advertise.id, true)} className='btn btn-info mx-2'>Ha</button>
                            <button onClick={()=>changeFound(advertise.id, false)} className='btn btn-danger'>Yo'q</button>
                        </div>
                    </div>)
                }
            </div>
        </div>
    );
}

export default Adminpage;
