import {createSlice} from "@reduxjs/toolkit";

const state = {
    posts: [],
    visible: false
}

const slice = createSlice({
    name: "post",
    initialState: state,
    reducers: {
        getPosts(){},
        getPostSuccess(state, action){
            state.posts = action.payload;
        },
        toggle(state, action){
            state.visible = !state.visible;
            console.log(state.visible);
        },
        addPost(){},
        deletePost(){},
        editPost(){
            toggle()
        },

    }
})

export const { getPostSuccess, getPosts, toggle, addPost, deletePost, editPost } = slice.actions
export default slice.reducer
