import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCategories, getProducts, setOrderProduct, deleteOrder, changeOrderproduct } from '../store/slice/userReducer';
import './index.scss'
import { useNavigate } from 'react-router-dom';

function Home() {

    const dispatch = useDispatch();
    const {categories, products, orderProducts } = useSelector(state => state.userReducer);
    let [arr, setArr] = useState([]);
    const navigate = useNavigate();

    useEffect(()=>{
        dispatch(getCategories());
    }, [])

    function addToBasket(product){
        // let a = orderProducts.filter(order => order.id == product.id);
        // if(a.length === 0){
        //     arr.push({...product, count: 1});
        // }
        // setArr([...arr])
        // console.log(arr);
        dispatch(setOrderProduct(product))
    

    }
    function increment( index){
        arr[index] = {...arr[index], count: arr[index].count+1}
        orderProducts[index] = {...orderProducts[index], count: orderProducts[index].count+1}
        
        
        dispatch(changeOrderproduct(orderProducts));
    }

    function decrement( index){
        console.log(index);
        arr[index] = {...arr[index], count: arr[index].count-1}
        setArr([...arr])
        
        dispatch(setOrderProduct(arr));
    }

    function orderDelete(index, order){
        console.log(index,order);
        dispatch(deleteOrder(index))
    }
   
    

    function goToBasket(){
        navigate("/order-product")
    }

    return (
        <div className='container'>
            <div className='row'>
                <div className="col-12 d-flex justify-content-between box">
                    <h1>Hello world</h1>
                    <button onClick={()=> goToBasket()} style={{height: "45px"}} className='btn btn-info'>card: {orderProducts.length}</button>
                </div>
                <div className="col-4">
                    {
                        categories.map(category=> <div onClick={()=> dispatch(getProducts(category.id))} className='box' key={category.id}>
                            <h5>{category.name}</h5>
                        </div>)
                    }
                </div>
                <div className="col-8">
                    <div className="row">
                        {
                            products.map(product => <div className='card' key={product.id}>
                                <div className="card-header">
                                    <h5>{product.name}</h5>
                                </div>
                                <div className="card-body">
                                    <img src={"http://localhost:8080/file/"+product.imgUrl} style={{display: "block"}} width={100} height={100} alt="" />
                                    <p>{product.amount} $</p>
                                </div>
                                <div className="card-footer">
                                    <button onClick={()=> addToBasket(product)} className='btn btn-success my-3 w-100'> + add to basket</button>
                                </div>
                            </div>)
                        }
                    </div>
                </div>
            </div>

            <div>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>amount</th>
                        <th>price</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        orderProducts.map((order, index)=> <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.name}</td>
                            <td>{order.amount * order.count}</td>
                            <td>
                                <button onClick={()=> increment (index)} className='btn btn-warning mx-1'>+</button>
                                {order.count} 
                                <button onClick={()=> decrement(index)} className='btn btn-warning mx-1'>-</button> 
                                </td>
                            <td>
                                <button onClick={()=> orderDelete(index, order)} className='btn btn-danger'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
            
        </div>
    );
}

export default Home;
