import React, { useEffect, useState, useContext } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../style/index.scss'
import '../../fontawesome-free-6.5.1-web/css/all.css'
import { apiCall } from '../../utils/apiCall';
import { Context } from '../../storage/Context';
function Home() {

    const [categories, setCategories] = useState([]);
    const [products, setProduct] = useState([]);
    const {state, dispatch} = useContext(Context);

    useEffect(()=>{
        apiCall('/category', 'get').then(res=>{
            setCategories(res.data)
        });

        apiCall("/product", "get").then(res=>{
            setProduct(res.data)
        })
    }, [])

    function addProductToCard(product, event){
        dispatch({type: "PUSH", payload: product})
        event.target.disabled = true;
        let products = localStorage.getItem("products") ? JSON.parse(localStorage.getItem("products")) : [];
        products.push(product);
        localStorage.setItem("products", JSON.stringify(products));
    }

    return (
        <div className='wrapper'>
            <div className='left'>
                <div className='category'>
                    <i className="fa-solid fa-bars"></i>
                </div>
                {
                    categories.map((category, index)=> <div key={category.id} className='category'>
                        <i className={'fa-solid fa-'+category.icon}></i>
                    </div>)
                }
                 <div className="settings">
                    <i className="fa-solid fa-gear"></i>
                </div>
            </div>
            <div className='right'>
                <div className='header'>
                    <h3>Products</h3>
                    <div className='actions'>
                        <button className='btn btn-warning mx-1'>My Orders</button>
                        <button className='btn btn-warning mx-1'>Card {state.productAmount.length}</button>
                        <button className='btn btn-danger mx-1'>Log Out</button>
                    </div>
                </div>
                <div className="content">
                    <div className="row px-5 py-2">
                        {
                            products.map((product, index)=><div key={product.id} className='card col-2 my-1 mx-1'>
                            <div className="card-header">
                                <h4 className='text-center'>{product.name}</h4>
                            </div>
                            <div className="card-body">
                                <img style={{margin: "auto", display: "block"}} width={100} src={product.imgUrl} alt="" />
                            </div>
                            <div className="card-footer">
                                <button  onClick={(event)=> addProductToCard(product, event)} className='btn btn-success w-100'> Add to card +</button>
                            </div>
                        </div>)
                        }
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
