import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall'

function App() {

    const [users, setUsers] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [counts, setCounts] = useState({})

    useEffect(() => {
        getUsers();
        getTasks();
        getStatusCounts();
    }, [])



    function getStatusCounts() {
        apiCall('/task/count', 'get').then(res => {
            setCounts(res.data);
            console.log(res.data);
        })
    }

    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data)
            console.log(res.data);
        })
    }

    function getTasks() {
        apiCall('/task/user', 'get').then(res => {
            setTasks(res.data)
            console.log(res.data);
        })
    }

    console.log(counts);

    return (
        <div>
            <div className="header d-flex">
                <div className="card">
                    <div className="card-header">
                        <h6>All</h6>
                        <h6>{counts.totalCount}</h6>
                    </div>
                </div>
               
            </div>
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>description</th>
                            <th>status</th>
                            <th>firstname</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map(task => <tr>
                                <td>{task.id}</td>
                                <td>{task.title}</td>
                                <td>{task.description}</td>
                                <td className={ 
                                    task.status === 'NON_STARTED' ?
                                     "bg-warning" : task.status === 'ON_HOLD' ? 
                                     "bg-info" : task.status === 'IN_PROGRESS' ?
                                     "bg-light" : task.status === 'COMPLETED' ?
                                     "bg-success" : task.status === 'CLOSED' ?
                                     "bg-danger" : ""  }>{task.status}</td>
                                <td>{task.firstName}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default App;
