    useEffect(()=>{
        getUsers();
        getTasks();
    }, [])



    function getUsers(){
        apiCall('/user', 'get').then(res=>{
            setUsers(res.data)
            console.log(res.data);
        })
    }

    function getTasks(){
        apiCall('/task/user', 'get').then(res=>{
            setTasks(res.data)
            console.log(res.data);
        })
    }

    return (
        <div>
            <div></div>
