import {createSlice} from "@reduxjs/toolkit";

const state = {}

const slice = createSlice({
    name: "payment",
    initialState: state,
    reducers: {
        addPayment(state, action){

        }
    }
})

export const {addPayment } = slice.actions;
export default slice.reducer;
