import { call, takeLatest, put } from "redux-saga/effects";
import { addPost, getPostSuccess, getPosts, deletePost } from "../slice/PostReducer";
import { apiCall } from "../slice/apiCall";
import { changeCompleted, getTodos, getTodosSuccess } from "../slice/todoReducer";


export function* workGetTodos(action){
    try{
        const res = yield call(apiCall, "/todo/"+action.payload, "get");
        yield put(getTodosSuccess(res.data));
    }catch(err){

    }
}

export function* workChangeCopleted(action){
    try{
        yield call(apiCall, "/todo", "put", action.payload);
        yield put(getTodos(action.payload.userId));
    }catch(err){

    }
}

export function* todoSaga(){
    yield takeLatest(getTodos().type, workGetTodos);
    yield takeLatest(changeCompleted().type, workChangeCopleted)
}
