import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../../utils/apiCall.js";
import { getCurrentUser, getCurrentUserSuccess, getUserAdvertise, getUserAdvertiseSuccess } from "../slice/userReducer.js";

export function* workGetUserAdvertise(action){
    const res = yield call(apiCall, "/api/lost/user", 'get');
    console.log(res.data);
    yield put(getUserAdvertiseSuccess(res.data));
}

export function* workGetCurrenUser(action){
    const res = yield call(apiCall, "/api/user", 'get');
    if(res.data.id){
        yield put(getCurrentUserSuccess(res.data))
    }
}

export function* userSaga() {
    yield takeLatest(getUserAdvertise().type, workGetUserAdvertise);
    yield takeLatest(getCurrentUser().type, workGetCurrenUser);
}
