import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [select, setSelect] = useState('All')

    useEffect(()=>{
        getPosts('page',1);
        getUsers();
    }, [])

    useEffect(()=>{
        getPostByPageNum(select)
        generatePageBtn(posts);
    }, [select])

    function getPosts(url, page){
        console.log(url, page);
        apiCall('/post/'+url+'/'+page+'?limit=5', 'get').then(res=>{
            console.log(res.data);
            setPosts(res.data)
        })
    }

    function getUsers(){
        apiCall('/user', 'get').then(res=>{
            setUsers(res.data);
        })
    }

    function getPostByPageNum(num){
        if(select==='All'){
            getPosts('page', num > 0 ? num : 1)
        }else{
            getPosts(select, num > 0 ? num : 1)
        }
    }

    function changeSelect(e){
        setSelect(e.target.value);
    }

    function generatePageBtn(posts){
        console.log(posts[0]?.pageCount);
        let a = posts.length > 0 ? posts[0].pageCount : 0
        let b = [];

        for(let i = 1; i<=a; i++){
            b.push(i);
        }

        return (
            <div>
                {
                    b.map(num=> <button onClick={()=>getPostByPageNum(num)} className="btn btn-info m-2" key={num}>{num}</button>)
                }
            </div>
        )
    }


    return (
        <div>
            <select onChange={(e)=>changeSelect(e)} name="" id="select">
                <option value="All">All</option>
                {
                    users.map(user=> <option value={user.id}>{user.fullName}</option>)
                }
            </select>

            {
                generatePageBtn(posts)
            }
        </div>
    )
}

export default Home;
