import {apiCall} from '../utils/apiCall'
import React, { useEffect, useState } from 'react';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'

function Home() {

    const [tasks, setTasks] = useState([]);
    const [ids, setIds] = useState([]);
    const [visible, setVisible] = useState(false);
    const [users, setUsers] = useState([]);

    useEffect(()=>{
        getTasks();
        getUsers();
    }, [])

    function getTasks(){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function getUsers(){
        apiCall('/user/all', 'get', '', localStorage.getItem('token')).then(res=>{
            setUsers(res.data)
        })
    }

    function toggle(){
        setVisible(prev=>!prev);
    }

    function deleteTask(){
        console.log(ids);
        apiCall('/task', 'delete', ids, localStorage.getItem("token")).then(res=>{
            console.log(res.data);
        })
    }

    function selectTask(id, e){
        if(e.target.checked){
            ids.push(id);
            setIds([...ids])
        }else{
            let filter = ids.filter((item)=> item!==id);
            setIds([...filter])

        }
        console.log(ids);
    }

    function saveTask(e){
        e.preventDefault();
        let data = {name: e.target[0].value, userId: e.target[1].value};
        apiCall('/task', 'post', data, localStorage.getItem('token')).then(res=>{
            console.log(res.data);
        })
    }

    function changeColor(status){
        return status === "NON_STARTED" ?
                "#EF8A0E" : 
                status === "ON_HOLD" ? 
                "#13B8F4" :
                status === "IN_PROGRESS" ?
                "#74BC40" :
                status === "CLOSED" ? 
                "#B9333F" :
                status === "COMPLETED" ?
                "#5DDCA4" : "white"
    }


    return (
        <div className='container'>
            <button onClick={toggle} className='btn btn-info'>add Task +</button>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>status</th>
                        <th>username</th>
                        <th>select</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        tasks.map(task=> <tr>
                            <td>{task.id}</td>
                            <td>{task.name}</td>
                            <td style={{background: changeColor(task.status)}}>{task.status}</td>
                            <td>{task.username}</td>
                            <td>
                                <input onChange={(e)=>selectTask(task.id, e)} type="checkbox" />
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
            <button className='btn btn-warning' onClick={deleteTask}>delete</button>
            <Rodal height={300} visible={visible} onClose={toggle}>
                    <div className="card mt-4">
                        <form onSubmit={saveTask} action="">
                            <div className="card-header">
                                <h5 className='text-center'>Add task</h5>
                            </div>
                            <div className="card-body">
                                <input type="text" className='form-control my-2' />
                                <select className='form-control' id="">
                                    {
                                        users.map(user=> <option value={user.id}>{user.username}</option>)
                                    }
                                </select>
                            </div>
                            <div className="card-footer">
                                <button className="btn btn-warning w-100 my-2">Submit</button>
                            </div>
                        </form>
                    </div>
            </Rodal>
        </div>
    );
}

export default Home;
