import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, Outlet, useNavigate } from "react-router-dom";
import Login from "./Login";

function Home() {

    const [currentUser, setCurrentUser] = useState({});
    const [myMessages, setMyMessages] = useState([]);

    useEffect(() => {
        getCurrentUser();
    }, [])


    useSubscription("/topic", (msg) => {
        console.log("salom");
    })

    function getCurrentUser() {
        apiCall('/user/' + localStorage.getItem("userId"), 'get').then(res => {
            console.log(res.data);
            setCurrentUser(res.data.data)
        })
    }

    function getAllMyMessages(){
        apiCall('/message/'+localStorage.getItem("userId"), 'get').then(res=>{
            console.log(res.data);
            setMyMessages(res.data)
        })
    }

    return (
        <div className="container">
            <div className="row">
                <div className="sider col-4 border">
                    <h4 className="text-center">Sider</h4>
                    <div className="card m-1">
                        <div className="card-header"> New Message</div>
                    </div>
                    <div className="card m-1">
                        <div className="card-header"> Send Messages: {currentUser.sendMessagesCount}</div>
                    </div>
                    <div className="card m-1">
                        <div className="card-header"> Inbox: {currentUser.inInboxMessagesCount}</div>
                    </div>
                    <div className="card m-1">
                        <div className="card-header"> Drafts: {currentUser.draftMessagesCount}</div>
                    </div>
                    <div className="card settings">
                        <div className="card-body">
                            <h6>{currentUser.firstName + " " + currentUser.lastName}</h6>
                            <div>@{currentUser.email}</div>
                            <div style={{color: currentUser.online ? "blue" : "gray"}}>{currentUser.online ? "online" : "offline"}</div>
                        </div>
                    </div>
                </div>
                <div className="col-8 border">
                    <div className="header">

                    </div>
                    <div className="content">
                        <Outlet/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home;
