import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getLostItems } from '../store/slice/lostItemReducer';
import { apiCall } from '../utils/apiCall';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'

function Home() {

    const {lostItems} = useSelector(state=>state.lostItemReducer);
    const dispatch = useDispatch();
    const [filter, setFilter] = useState({
        holat: '',
        type: '',
        subType: '',
        region: '',
        district: ''
    });
    const [types, setTypes] = useState([]);
    const [subTypes, setSubTypes] = useState([]);
    const [regions, setRegions] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [visible, setVisible] = useState(false);

    useEffect(()=>{
        dispatch(getLostItems(filter));
        getTypes();
        getRegions();
        console.log(lostItems);
    }, [])


    function getTypes(){
        apiCall('/api/type', 'get', null, {}).then(res=>{
            setTypes(res.data)
        })
    }
    function getSubTypes(typeId){
        apiCall('/api/type/subtype/'+typeId, 'get', null, {}).then(res=>{
            setSubTypes(res.data)
        })
    }

    function getRegions(){
        apiCall('/api/type/region', 'get', null, {}).then(res=>{
            setRegions(res.data)
        })
    }

    function getDistrict(regionId){
        apiCall('/api/type/district/'+regionId, 'get', null, {}).then(res=>{
            setDistricts(res.data)
        })
    }
    
    function changeType(e){
        setFilter({...filter, type: e.target.value});
        getSubTypes(e.target.value)
        dispatch(getLostItems({...filter, type: e.target.value}));
    }

    function changeRegion(e){
        setFilter({...filter, region: e.target.value});
        getDistrict(e.target.value);
        console.log(filter);
        dispatch(getLostItems({...filter, region: e.target.value}));
    }

    function toggle(){
        setVisible(prev=>!prev);
    }

    function reviewLostItem(lostItem){
        toggle();
    }

    return (
        <div>
            <div>
                <div>
                    <div>
                        <select onChange={(e)=>{setFilter({...filter, holat: e.target.value}); dispatch(getLostItems({...filter, holat: e.target.value}))}} name="" id="">
                            <option value="">Holatni tanlang</option>
                            <option value="YOQOTIB_QOYILGAN">YOQOTIB_QOYILGAN</option>
                            <option value="TOPIB_OLINGAN">TOPIB_OLINGAN</option>
                        </select>
                    </div>
                    <div>
                        <select onChange={(e)=>changeType(e)} name="" id="">
                            <option value="">Ashyo turini tanlang</option>
                            {
                                types.map(type=> <option value={type.id}>{type.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select onChange={(e)=>{setFilter({...filter, subType: e.target.value}); dispatch(getLostItems({...filter, subType: e.target.value}));}} name="" id="">
                            <option value="">Ashyo sub turini tanlang</option>
                            {
                                subTypes?.map(subtype=><option value={subtype.id}>{subtype.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select onChange={(e)=>changeRegion(e)} name="" id="">
                            <option value="">Hududni tanlang</option>
                            {
                                regions.map(region=><option value={region.id}>{region.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select onChange={(e)=>{setFilter({...filter, district: e.target.value}); dispatch(getLostItems({...filter, district: e.target.value}));}} name="" id="">
                            <option value="">Tumanni tanlang</option>
                            {
                                districts?.map(district=><option value={district.id}>{district.name}</option>)
                            }
                        </select>
                    </div>
                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>Status</th>
                            <th>Buyum turi</th>
                            <th>Buyum sub turi</th>
                            <th>Viloyat</th>
                            <th>Tuman</th>
                            <th>Tashkilot</th>
                            <th>Desciption</th>
                            <th>Sana</th>
                            <th>Reyestrdagi raqami</th>
                            <th>actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            lostItems.map(lostItem=> <tr>
                                <td>{lostItem.id}</td>
                                <td>{lostItem.status}</td>
                                <td>{lostItem.type}</td>
                                <td>{lostItem.subType}</td>
                                <td>{lostItem.region}</td>
                                <td>{lostItem.district}</td>
                                <td>{lostItem.organization}</td>
                                <td>{lostItem.description}</td>
                                <td>{lostItem.lostDate}</td>
                                <td>{lostItem.registry}</td>
                                <td>
                                    <button onClick={()=>reviewLostItem(lostItem)} className='btn btn-success'>Rasm solish</button>
                                </td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
            <Rodal height={450} width={1000} visible={visible} onClose={toggle}>

            </Rodal>
        </div>
    )
}

export default Home;
