import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getUser, getUserSuccess } from "../slice/userReducer.js";


export function* workGetUsers(action){
    try{
        const res = yield call(apiCall, "/auth", "get");
        yield put(getUserSuccess(res.data))
    }catch(err){

    }
}





export function* userSaga() {
    yield takeLatest(getUser().type, workGetUsers);
}
