import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import {addUser, adduserSuccess, deleteUser, getCurrentUser,getCurrentUserById,getCurrentUserSuccess, searchUserByPhoneNumber, searchUserSuccess} from "../slice/userReducer.js";

export function* workGetCurrentUser(action){
    let datas={phoneNumber: action.payload.value.phoneNumber, password: action.payload.value.password};
    const res = yield call(apiCall, "/user/login", "post", datas);
    if(res.data.message === "Success"){
        action.payload.navigate('/home');
        localStorage.setItem("token", JSON.stringify(res.data.object.id))
    }
    yield put(getCurrentUserSuccess(res.data.object))
}

export function* workGetCurrentUserById(action){
    const res = yield call(apiCall, "/user/"+action.payload, 'get');
    yield put(getCurrentUserSuccess(res.data));
}

export function* workSearchUser(action){
    const res = yield call(apiCall, "/user/search?phoneNumber="+action.payload, "get");
    yield put(searchUserSuccess(res.data.object));
}

export function* workAddUser(action){
    const res = yield call(apiCall, "/user", "post", action.payload.data);
    if(res.data.message === "Success"){
        action.payload.navigate("/")
    }
}

export function* workDeleteUser(action){
    const res = yield call(apiCall, "/user/"+action.payload.userId, "delete", );
    if(res.data === "Deleted user"){
        action.payload.navigate("/")
    }
}

export function* userSaga() {
    yield takeLatest(getCurrentUser().type, workGetCurrentUser);
    yield takeLatest(searchUserByPhoneNumber().type, workSearchUser);
    yield takeLatest(addUser().type, workAddUser);
    yield takeLatest(deleteUser().type, workDeleteUser);
    yield takeLatest(getCurrentUserById().type, workGetCurrentUserById)
}
