import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../../utils/apiCall.js";
import {
    getClasses, 
    getCurrentUser, 
    getCurrentUserSuccess, 
    getClassesSuccess,
    getSciences,
    getSciencesSuccess,
    getLessons,
    getLessonsSuccess
} from "../slice/userReducer.js";

export function* workGetCurrentUser(action){
    const res = yield call(apiCall, '/user/token', 'get');
    yield put(getCurrentUserSuccess(res.data))
}

export function* wordGetClasses(action){
    const res = yield call(apiCall, "/lesson", 'get');
    yield put(getClassesSuccess(res.data))
}

export function* wordGetSciences(action){
    console.log(action.payload);
    const res = yield call(apiCall, "/lesson/science/"+action.payload, 'get');
    console.log(res.data);
    yield put(getSciencesSuccess(res.data))
}

export function* wordGetLessons(action){
    const res = yield call(apiCall, "/lesson/lessons/"+action.payload, 'get');
    yield put(getLessonsSuccess(res.data))
}

export function* userSaga() {
    yield takeLatest(getCurrentUser().type, workGetCurrentUser);
    yield takeLatest(getClasses().type,  wordGetClasses);
    yield takeLatest(getSciences().type, wordGetSciences);
    yield takeLatest(getLessons().type, wordGetLessons)
}
