import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addExpanseToUser, addInCome, getUsers } from './store/slice/userReducer';
import './index.scss'

function App() {

    const { users } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers());
    }, [])


    function addInComed() {
        let select = document.getElementById("income");
        let value = document.getElementById('amount');
        dispatch(addInCome({ receivingUserId: Number(select.value), moneyAmount: value.value }));
    }

    function addExpanse() {
        let receivingId = document.getElementById("receivingId")
        let givingId = document.getElementById("givingId")
        let moneyAmount = document.getElementById("moneyAmount")
        dispatch(addExpanseToUser({ receivingUserId: Number(receivingId.value), givingUserId: Number(givingId.value), moneyAmount: Number(moneyAmount.value) }))
    }

    return (
        <div>
            <div className='card col-3 m-3'>
                <div className="card-header">
                    <select className='form-control' id='income'>
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                </div>
                <div className="card-body">
                    <input id='amount' className='form-control' type="text" />
                </div>
                <div className="card-footer">
                    <button className='btn btn-success w-100' onClick={addInComed}>send</button>
                </div>
            </div>
            <div>
                <div className='wrapper'>
                    from: <select className='select' name="" id="givingId">
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                    to: <select className='select' name="" id="receivingId">
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                    amount: <input className='inp' type="text" id='moneyAmount' />
                    <button className='btn btn-success' onClick={addExpanse}>save</button>
                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>phone number</th>
                            <th>money</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => <tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.phoneNumber}</td>
                                <td>{user.moneyAmount}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
