import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../style/index.scss'
import '../../fontawesome-free-6.5.1-web/css/all.css'
import { apiCall } from '../../utils/apiCall';
function Home() {

    const [productAmount, setProductAmount] = useState(0);
    const [categories, setCategories] = useState([]);

    useEffect(()=>{
        apiCall('/category', 'get').then(res=>{
            setCategories(res.data)
            console.log(res.data);
        })
    }, [])

    return (
        <div className='wrapper'>
            <div className='left'>
                <div className='category'>
                    <i className="fa-solid fa-bars"></i>
                </div>
                {
                    categories.map((category, index)=> <div key={category.id} className='category'>
                        <i className={'fa-solid fa-'+category.icon}></i>
                    </div>)
                }
                 <div className="settings">
                    <i className="fa-solid fa-gear"></i>
                </div>
            </div>
            <div className='right'>
                <div className='header'>
                    <h3>Products</h3>
                    <div className='actions'>
                        <button className='btn btn-warning mx-1'>My Orders</button>
                        <button className='btn btn-warning mx-1'>Card {productAmount}</button>
                        <button className='btn btn-danger mx-1'>Log Out</button>
                    </div>
                </div>
                <div className="content">
                    <div className="row px-4 py-2">
                        <div className='card col-3 my-1 mx-1'>
                            <div className="card-header">
                                <h4>Coca cola</h4>
                            </div>
                            <div className="card-body">
                                <img width={100} src={"https://mir-s3-cdn-cf.behance.net/project_modules/1400_opt_1/b0998a26210243.563531caac4f7.png"} alt="" />
                            </div>
                            <div className="card-footer">
                                <button className='btn btn-success w-100'> Add to card</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
