import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCategories, getProducts, setOrderProduct } from '../store/slice/userReducer';
import './index.scss'

function Home() {

    const dispatch = useDispatch();
    const {categories, products, orderProducts } = useSelector(state => state.userReducer);
    const [arr, setArr] = useState([]);

    useEffect(()=>{
        dispatch(getCategories());
    }, [])

    function addToBasket(product){
        let a = orderProducts.filter(order => order.id == product.id);
        if(a.length === 0){
            arr.push({...product, price: 1});
        }
        setArr([...arr])
        dispatch(setOrderProduct(arr))

    }
    

    return (
        <div className='container'>
            <div className='row'>
                <div className="col-12 d-flex justify-content-between box">
                    <h1>Hello world</h1>
                    <button style={{height: "45px"}} className='btn btn-info'>card: {orderProducts.length}</button>
                </div>
                <div className="col-4">
                    {
                        categories.map(category=> <div onClick={()=> dispatch(getProducts(category.id))} className='box' key={category.id}>
                            <h5>{category.name}</h5>
                        </div>)
                    }
                </div>
                <div className="col-8">
                    <div className="row">
                        {
                            products.map(product => <div className='card' key={product.id}>
                                <div className="card-header">
                                    <h5>{product.name}</h5>
                                </div>
                                <div className="card-body">
                                    <img src={"http://localhost:8080/file/"+product.imgUrl} style={{display: "block"}} width={100} height={100} alt="" />
                                    <p>{product.amount} $</p>
                                </div>
                                <div className="card-footer">
                                    <button onClick={()=> addToBasket(product)} className='btn btn-success my-3 w-100'> + add to basket</button>
                                </div>
                            </div>)
                        }
                    </div>
                </div>
            </div>
            
        </div>
    );
}

export default Home;
