import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';

function App() {

    const [users, setUsers] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [counts, setCounts] = useState({});
    const [taskId, setTaskId] = useState([]);

    useEffect(() => {
        getUsers();
        getTasks();
        getStatusCounts();
    }, [])



    function getStatusCounts() {
        apiCall('/task/count', 'get').then(res => {
            setCounts(res.data);
            console.log(res.data);
        })
    }

    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data)
            console.log(res.data);
        })
    }

    function getTasks() {
        apiCall('/task/user', 'get').then(res => {
            setTasks(res.data)
            console.log(res.data);
        })
    }

    function deleteTodos() {
        apiCall('/task/all', 'delete', taskId).then(res => {
            getTasks();
        })
    }

    function searchTodos() {
        let search = document.getElementById('search');
        apiCall('/task/search?title='+search.value, 'get').then(res=>{
            setTasks(res.data)
        })
    }

    function selectTodos(id, e){
        let idd = taskId.filter(ids=> ids===id);
        if(e.target.checked){
            if(idd.length === 0){ taskId.push(id); console.log(taskId)};

        }else{

        }
        setTaskId([...taskId])
    }

    console.log(counts);

    return (
        <div>
            <div className="header d-flex">
                <div className="card">
                    <div onClick={getTasks} className="card-header">
                        <h6>All</h6>
                        <h6>{counts.totalCount}</h6>
                    </div>
                </div>
                {
                    counts.totalCount ? counts.statusList.map(list => <div className={`card ${list.status === "NON_STARTED" ?
                        "bg-warning" : list.status === 'ON_HOLD' ?
                            "bg-info" : list.status === 'IN_PROGRESS' ?
                                "bg-light" : list.status === 'COMPLETED' ?
                                    "bg-success" : list.status === 'CLOSED' ?
                                        "bg-danger" : ""}`}>
                        <div className="card-header">
                            <h6>{list.status}</h6>
                            <h6>{list.count}</h6>
                        </div>
                    </div>) : ""
                }
            </div>
            <div>
                <button onClick={deleteTodos} className="btn btn-danger m-2">Clear Todos</button>
                <div className="w-50 d-flex">
                    <input type="text" id="search" className="form-control w-25 mx-3" placeholder="Search..." />
                    <button onClick={searchTodos} className="btn btn-light ">submit</button>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th><input type="checkbox" /></th>
                            <th>t/r</th>
                            <th>title</th>
                            <th>description</th>
                            <th>status</th>
                            <th>firstname</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map(task => <tr>
                                <td><input onChange={(e)=>selectTodos(task.id, e)} type="checkbox" /></td>
                                <td>{task.id}</td>
                                <td>{task.title}</td>
                                <td>{task.description}</td>
                                <td className={
                                    task.status === 'NON_STARTED' ?
                                        "bg-warning" : task.status === 'ON_HOLD' ?
                                            "bg-info" : task.status === 'IN_PROGRESS' ?
                                                "bg-light" : task.status === 'COMPLETED' ?
                                                    "bg-success" : task.status === 'CLOSED' ?
                                                        "bg-danger" : ""}>{task.status}</td>
                                <td>{task.firstName}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default App;
