import React, { useEffect } from 'react';
import './index.css'
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';


function Header() {

    const {currentUser} = useSelector(state => state.userReducer);
    const navigate = useNavigate();

    function goToSaveLostItem(){
        navigate('/save')
    }

    useEffect(()=>{
        console.log(currentUser);
    }, [currentUser])

    return (
        <div className="header">
            <div className="logo">
                <h3>Topilmalar</h3>
            </div>
            <div className="navbar">
                <div className="sport">
                    <p onClick={()=>{navigate('/main')}}><strong>Reyestr</strong></p>
                </div>
                <div className="lessons">
                    <p onClick={()=>{navigate('/')}}><strong>Statistika</strong></p>
                </div>
                <div className='themes'>
                    <p><strong>Tizim haqida</strong></p>
                </div>
                <div className='themes'>
                    <p><strong>Yordam</strong></p>
                </div>
            </div>
            <div className="actions">
                <div>
                    <button onClick={goToSaveLostItem} className='btn btn-outline-info m-2'>Ariza berish</button>
                    {currentUser.username ? <button onClick={()=>{localStorage.clear()}}  className='btn btn-danger '>Chiqish</button> : <button onClick={()=>{navigate('/login')}} className='btn btn-info '>Kirish</button>}
                </div>
            </div>
        </div>
    );
}

export default Header;
