import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getOldUsers, getTwoRich, getUsers, getUsersSuccess, searchUsers } from "../slice/userReducer.js";


export function* workGetUsers(action){
    const res = yield call(apiCall, "/user?limit="+action.payload.limit+"&pageIndex="+action.payload.pageIndex, "get");
    yield put(getUsersSuccess(res.data));
}

export function* workGetTwoRich(action){
    const res = yield call(apiCall, "/user/two-rich?limit="+action.payload, "get");
    yield put(getUsers())
}

export function* workGetOldUsers(action){
    const res = yield call(apiCall, "/old-user?age="+action.payload.age+"&limit="+action.payload.limit+"&pageIndex="+action.payload.pageIndex, "get");
    yield put(getUsers())
}

export function* workSearchUsers(action){
    const res = yield call(apiCall, "/search?limit="+action.payload.limit+"&pageIndex="+action.payload.pageIndex, "get");
    yield put(getUsers())
}

export function* userSaga() {
    yield takeLatest(getUsers().type, workGetUsers);
    yield takeLatest(getTwoRich().type, workGetTwoRich);
    yield takeLatest(getOldUsers().type, workGetOldUsers);
    yield takeLatest(searchUsers().type, workSearchUsers)
}
