import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCategories, getProducts } from '../store/slice/userReducer';
import './index.scss'

function Home() {

    const dispatch = useDispatch();
    const {categories} = useSelector(state => state.userReducer);

    useEffect(()=>{
        dispatch(getCategories());
    }, [])
    

    return (
        <div className='container'>
            <div className='row'>
                <div className="col-12 box">
                    <h1>Hello world</h1>
                </div>
                <div className="col-4">
                    {
                        categories.map(category=> <div onClick={()=> dispatch(getProducts(category.id))} className='box' key={category.id}>
                            <h5>{category.name}</h5>
                        </div>)
                    }
                </div>
                <div className="col-8">

                </div>
            </div>
            
        </div>
    );
}

export default Home;
