import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPayments } from '../store/slice/paymentReducer';
import { getLoans } from '../store/slice/loanReducer';

function Payment() {

    const {payments} = useSelector(state => state.paymentReducer);
    const {currentUser} = useSelector(state => state.userReducer);
    const {loans} = useSelector(state => state.loanReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getPayments(currentUser.id))
        dispatch(getLoans(currentUser.id))

        console.log(payments, loans);
    }, [])

    return (
        <div>
            <div>
                <h3>Qarzlar</h3>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>loan id</th>
                            <th>amount</th>
                            <th>date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            loans.map((loan, index)=><tr key={loan.id}>
                                <td>{index+1}</td>
                                <td>{loan.id}</td>
                                <td>{loan.amount}</td>
                                <td>{loan.date}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
            <div>
                <h3>To'langan summalar</h3>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>payment id</th>
                            <th>loan id</th>
                            <th>amount</th>
                            <th>date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            payments.map((payment, index)=><tr key={payment.id}>
                                <td>{index+1}</td>
                                <td>{payment.id}</td>
                                <td>{payment.loanId}</td>
                                <td>{payment.amount}</td>
                                <td>{payment.date}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Payment;
