import React, { useEffect, useState } from "react";
import {apiCall} from './utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import 'bootstrap-icons/font/bootstrap-icons.css'

function App(){

    const [tasks, setTasks] = useState([]);
    const [categories, setCategories] = useState([]);
    const [visible, setVisible] = useState(false);
    const [visible2, setVisible2] = useState(false);
    const [editCategoryName, setEditCategoryName] = useState({id: 0, edit: false});

    useEffect(()=>{
        getCategories();
        getTasks();
    }, [])

    function getCategories(){
        apiCall('/category', 'get').then(res=>{
            setCategories(res.data)
            console.log(res.data);
        })
    }

    function toggle(){
        setVisible(prev=>!prev)
    }

    function toggle2(){
        setVisible2(prev=>!prev)
    }

    function getTasksById(id){
        apiCall('/task/'+id, 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function getTasks(id){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function submit(e){
        e.preventDefault();
        if(editCategoryName.edit){
            apiCall("/category", 'put', {id: editCategoryName.id, name: e.target[0].value}).then(res=>{
                getCategories();
                setEditCategoryName({id: 0, edit: false})
            })
        }else{
            apiCall("/category", 'post', {name: e.target[0].value}).then(res=>{
                getCategories();
            })
        }
        e.target[0].value = "";
        toggle();
    }

    function editCategory(category){
        let categoryForm = document.getElementById('category');
        categoryForm[0].value = category.name;  
        setEditCategoryName({id: category.id, edit: true})
        toggle();
    }

    function submit2(e){

    }

    return (
        <div className="container">
            <div className="row">
                <div className="sider col-3">
                    <button onClick={toggle} className="btn btn-warning w-100 my-2">add Category</button>
                    {
                        categories.map(category=> <div className="card my-1">
                            <p className="card-header d-flex justify-content-between">{category.name} <i onClick={()=>editCategory(category)} className="bi bi-pencil"></i></p>
                        </div>)
                    }
                </div>
                <div className="content col-9">
                    <div className="header">
                        <button onClick={toggle2} className="btn btn-info w-25 my-2">add Task</button>
                    </div>
                    <div className="body">
                        <table className="table">
                            <thead>
                                <th>t/r</th>
                                <th>name</th>
                                <th>priority</th>
                                <th>category</th>
                                <th>date</th>
                                <th>active</th>
                            </thead>
                            <tbody>
                                {
                                    tasks.length ? tasks.map(task=><tr>
                                        <td>{task.id}</td>
                                        <td>{task.name}</td>
                                        <td>{task.priority}</td>
                                        <td>{task.category.name}</td>
                                        <td>{task.date}</td>
                                        <td>
                                            <input type="checkbox" checked={task.active} />
                                        </td>
                                    </tr>) : ""
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            <Rodal height={140} visible={visible} onClose={toggle} >
                <form id="category" onSubmit={submit} action="">
                    <input type="text" placeholder="Name..." className="form-control mt-3" />
                    <button className="btn btn-warning w-100 my-2">submit</button>
                </form>
            </Rodal>

            <Rodal height={250} visible={visible2} onClose={toggle2} >
                <form id="category" onSubmit={submit2} action="">
                    <input type="text" placeholder="Name..." className="form-control mt-4" />
                    <input type="text" placeholder="Priority..." className="form-control my-2" />
                    <select className="form-control my-2" name="" id="">
                        {
                            categories.map(category=> <option value={category.id}>{category.name}</option>)
                        }
                    </select>
                    <button className="btn btn-warning w-100 my-2">submit</button>
                </form>
            </Rodal>
        </div>
    )
}

export default App;
