import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import { useLocation } from "react-router-dom";

function Posts(){
    const [posts, setPosts] = useState([]);
    const location = useLocation();
    useEffect(()=>{
        getPosts();
        console.log(posts);
    }, [])
    function getPosts(){
        apiCall('/post/'+location.pathname.slice(location.pathname.length-1)+"?limit=5&page=1", 'get').then(res=>{
            setPosts(res.data.data)
        })
    }

    return (
        <div className="row">
            <div className="col-12">

            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>title</th>
                        <th>body</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        posts.map(post=> <tr>
                            <td>{post.id}</td>
                            <td>{post.title}</td>
                            <td>{post.body}</td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    )
}

export default Posts;
