import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [select, setSelect] = useState('All')

    useEffect(()=>{
        getPosts(0,1);
        getUsers();
    }, [])

    function getPosts(id, page){
        console.log(id, page);
        let url = id > 0 ? '/'+id : '';
        apiCall('/post'+url+'?pageNumber='+page+'&limit=5', 'get').then(res=>{
            console.log(res.data);
            setPosts(res.data)
        })
    }

    function changeSelect(e){

    }

    function getUsers(){
        apiCall('/user', 'get').then(res=>{
            setUsers(res.data);
        })
    }


    return (
        <div>
            <select onChange={(e)=>changeSelect(e)} name="" id="select">
                <option value="All">All</option>
                {
                    users.map(user=> <option value={user.id}>{user.fullName}</option>)
                }
            </select>
        </div>
    )
}

export default Home;
