import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from "../store/slice/userReducer"
import Rodal from 'rodal';

function Loan() {

    const { users } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers())
    }, [])


    return (
        <div>

            <table className='table'>
                <thead>
                    <tr>
                        <th>T/r</th>
                        <th>full name</th>
                        <th>phone number</th>
                        <th>loan amunt</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        users.map((user, index) => <tr key={user.id}>
                            <td>{index+1}</td>
                            <td>{user.firstname + " " + user.lastname}</td>
                            <td>{user.phoneNumber}</td>
                            <td>{user.loanAmount}</td>
                            <td>
                                <button className='btn btn-success mx-1'>+</button>
                                <button className='btn btn-success mx-1'>-</button>
                                <button className='btn btn-success mx-1'>history</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>

            <Rodal visible={visible} onClose={dispatch(toggle())}>
                <form>
                    <input type="text" />
                    <button className='btn btn-warning'>send</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Loan;
