import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../../utils/apiCall.js";
import {getLostItems, getLostItemsSuccess} from '../slice/lostItemReducer.js'

export function* workGetLostItems(action){
    let param = "?type="+action.payload.type+"&subType="+action.payload.subtype+"&region="+action.payload.region+"&district="+action.payload.district;
    let url = "/api/lost/filter";

    if(action.payload.condition || action.payload.type || action.payload.subType || action.payload.region || action.payload.district){
        param += "?";
        if(action.payload.condition){param += "condition="+action.payload.condition+"&"}
        if(action.payload.type){param+="type="+action.payload.type+"&"}
        if(action.payload.subType){param+="subType="+action.payload.subType+"&"}
        if(action.payload.region){param+="region="+action.payload.region+"&"}
        if(action.payload.district){param+="district="+action.payload.district+"&"}
        param += "number=5";
        url+=param;
    }else{
        url = "/api/lost/all";
    }

    const res = yield call(apiCall, url, 'get', null, {});
    yield put(getLostItemsSuccess(res.data));
}

export function* lostItemSaga(){
    yield takeLatest(getLostItems().type, workGetLostItems);
}
