import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [currentUser, setCurrentUser] = useState({});
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState({id: 0, content: "", comments: [], index: undefined});
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        getCurrentUser();
        getPosts();
    }, [])

    function getCurrentUser() {
        apiCall('/user', 'get', {}, "userId").then(res => {
            setCurrentUser(res.data);
        })
    }

    function toggle() {
        setVisible(prev => !prev);
    }

    function getPosts() {
        apiCall('/post', 'get').then(res => {
            setPosts(res.data);
        })
    }

    function addLike(postId){
        apiCall('/like', 'post', {postId, userId: Number(localStorage.getItem("userId"))})
    }

    function getComments(comment, content, id, index) {

        setComments({id, content, comments: comment, index});
        toggle();
    }

    function addComment(e){
        if(e.target.value.length > 2 && e.charCode===13){
            apiCall('/comment', 'post', {content: e.target.value, postId: comments.id, userId: Number(localStorage.getItem("userId"))}).then(res=>{
                getPosts();
                setComments({...comments, comments: posts[comments.index].comments})
                e.target.value = "";
            })
        }
    }

    return (
        <div className="wrapper row" style={{ backgroundColor: "#EBF6FF" }}>
            <div className="row">
                <div className="sider  col-4 border">
                    <div style={{ height: "80vh" }}>
                    </div>
                    {
                        currentUser.id ? <div className="card m-2">
                            <div className="card-header">
                                <h6>{currentUser.firstName + " " + currentUser.lastName}</h6>
                                <p>{currentUser.email}</p>
                            </div>
                        </div> : ""
                    }
                </div>
                <div className="content col-8 ">
                    {
                        posts.map((post, index) => <div className="card col-6 my-2">
                            <div className="card-body">
                                <p>{post.content}</p>
                                <p> <span className="profile">{post.fullName[0]}</span> {post.fullName}</p>
                            </div>
                            <div className="card-footer foot">
                                <i onClick={()=>addLike(post.id)} style={{ color: post.active ? "red" : "", left: "20px" }} className={post.active ? "bi bi-heart-fill" : "bi bi-heart"}></i>
                                <i onClick={()=>getComments(post.comments, post.content, post.id, index)} style={{ left: "50px", top: "-11px" }} className="bi bi-chat-dots m-2"></i>
                                <i style={{ left: "85px", top: "-8px" }} className="bi bi-send m-2"></i>
                            </div>
                        </div>)
                    }
                </div>
            </div>
            <Rodal width={500} height={400} visible={visible} onClose={toggle} >
                <div className="comment mt-4">
                    <h5>{comments.content}</h5>
                {

                    comments.id ? 
                    comments.comments.map(comment=> <div className="card my-2">
                        <div className="card-body d-flex justify-content-between">
                            <p><span style={{fontWeight: 500}}>@{comment.email}</span> {comment.content}</p>
                            <i style={{ color: comment.active ? "red" : "", left: "20px", fontSize: "20px" }} className={comment.active ? "bi bi-heart-fill" : "bi bi-heart"}></i>


                        </div>
                    </div> ) : ""
                }
                </div>

                <input type="text" onKeyPress={addComment} className="form-control my-3" />
            </Rodal>
        </div>
    )
}

export default Home;
