import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall'

function App() {

    const [users, setUsers] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [counts, setCounts] = useState({})

    useEffect(() => {
        getUsers();
        getTasks();
        getStatusCounts();
    }, [])



    function getStatusCounts(){
        apiCall('/task/count', 'get').then(res=>{
            setCounts(res.data);
            console.log(res.data);
        })
    }

    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data)
            console.log(res.data);
        })
    }

    function getTasks() {
        apiCall('/task/user', 'get').then(res => {
            setTasks(res.data)
            console.log(res.data);
        })
    }

    return (
        <div>
            <div className="header d-flex">
                        <div className="card-header">
                            <h6>All</h6>
                            <h6>{counts.totalCount}</h6>
                        </div>
                {
                    counts.statusList.map(count=> <div className="card">
                        <div className="card-header">
                            <h6>{count.status}</h6>
                            <h6>{count.count}</h6>
                        </div>
                    </div>)
                }
            </div>
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>description</th>
                            <th>status</th>
                            <th>firstname</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            tasks.map(task=> <tr>
                                <td>{task.id}</td>
                                <td>{task.title}</td>
                                <td>{task.description}</td>
                                <td>{task.status}</td>
                                <td>{task.firstName}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default App;
