import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { 
    getCashBalanceSuccess, 
    getCashBalance,
    getTotalBalanceSuccess, 
    getTotalBalance,
    getUsers, 
    getUsersSuccess,
    getCardBalance,
    getCardBalanceSuccess,
    getOddBalance,
    getOddBalanceSuccess,
    getEvenBalance,
    getEvenBalanceSuccess

} from "../slice/userReducer.js";


export function* workGetUsers(action){
    const res = yield call(apiCall, "/user"+action.payload.param,  "get");
    yield put(getUsersSuccess(res.data));
}

export function* workGetTotalBalance(action){
    const res = yield call(apiCall, "/user/total-balance", "get");
    yield put(getTotalBalanceSuccess(res.data));
}

export function* workGetCashBalance(action){
    const res = yield call(apiCall, "/user/cash-balance", "get");
    yield put(getCashBalanceSuccess(res.data));
}

export function* workCardTotalBalance(action){
    const res = yield call(apiCall, "/user/card-balance", "get");
    yield put(getCardBalanceSuccess(res.data));
}

export function* workOddTotalBalance(action){
    const res = yield call(apiCall, "/user/odd-balance", "get");
    yield put(getUsersSuccess(res.data));
}

export function* workEvenTotalBalance(action){
    const res = yield call(apiCall, "/user/even-balance", "get");
    yield put(getUsersSuccess(res.data));
}

