import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCategories, getProducts } from '../store/slice/userReducer';
import './index.scss'

function Home() {

    const dispatch = useDispatch();
    const {categories, products} = useSelector(state => state.userReducer);

    useEffect(()=>{
        dispatch(getCategories());
    }, [])
    

    return (
        <div className='container'>
            <div className='row'>
                <div className="col-12 box">
                    <h1>Hello world</h1>
                </div>
                <div className="col-4">
                    {
                        categories.map(category=> <div onClick={()=> dispatch(getProducts(category.id))} className='box' key={category.id}>
                            <h5>{category.name}</h5>
                        </div>)
                    }
                </div>
                <div className="col-8">
                    <div className="row">
                        {
                            products.map(product => <div className='card' key={product.id}>
                                <div className="card-header">
                                    <h5>{product.name}</h5>
                                </div>
                                <div className="card-body">
                                    <img src={"http://localhost:8080/file/"} style={{display: "block"}} width={100} height={100} alt="" />
                                    <p>{product.amount}</p>
                                </div>
                                <div className="card-footer">
                                    <button className='btn btn-success my-3 w-100'> + add to basket</button>
                                </div>
                            </div>)
                        }
                    </div>
                </div>
            </div>
            
        </div>
    );
}

export default Home;
