import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getChats, getChatsSuccess, getCurrentChat, getCurrentChatSuccess } from "../slice/chatReducer.js";
import { getMessages } from "../slice/messageReducer.js";

export function* workGetCurrentChat(action){
    const res = yield call(apiCall, "/chat/create", "post", action.payload);
    console.log(res.data);
    yield put(getCurrentChatSuccess(res.data))
    yield put(getMessages(res.data.chatId));
}

export function* workGetChats(action){
    const res = yield call(apiCall, "/chat/"+action.payload, "get");
    yield put(getChatsSuccess(res.data));
}

export function* chatSaga() {
    yield takeLatest(getCurrentChat().type, workGetCurrentChat);
    yield takeLatest(getChats().type, workGetChats);
}
